<?phpif(!isset($_SESSION))	session_start();//require_once("verificar_sesion.php");require_once("clases/AccesoDatos.php");require_once("clases/Materiales.php");require_once('clases/lib/nusoap.php');$queMuestro = isset($_POST['queMuestro']) ? $_POST['queMuestro'] : NULL;$host   = 'http://localhost/ejercicioFinal/clases/SERVIDOR/wsMateriales.php';$client = new nusoap_client($host . '?wsdl');		switch ($queMuestro) {        case "ENUNCIADO":                            require_once("enunciado.php");                    break;    case "MOSTRAR_CD":                    require_once("GrillaCd.php");                    break;    case "MOSTRAR_GRILLA":                    require_once("formGrilla.php");                    break;    case "FORM"://MUESTRA FORM ALTA-MODIFICACION USUARIO                    $material = isset($_POST["material"]) ? json_decode(json_encode($_POST["material"])) : NULL;		                    require_once("form.php");                    break;    case "ALTA_MATERIAL":                    $obj = json_decode(json_encode($_POST['material']));                    $obj->Exito = TRUE;                    $obj->Mensaje = "";                                        $cantidad = $client->call('AltaMaterial',array('nombre' => $obj->Nombre,'precio' => $obj->Precio, 'tipo' =>$obj->Tipo));                    if($cantidad > 0) {                        $obj->Exito = TRUE;                        $obj->Mensaje = "Fue agregado Satisfactorialmente";                                            } else {                                                $obj->Exito = FALSE;                        $obj->Mensaje = "ERROR al agregar";                    }                    echo json_encode($obj);                                        break;              case "MODIFICAR_MATERIAL":                            $obj = new stdClass();                    $obj = json_decode(json_encode($_POST['material']));                    $obj->Exito = TRUE;                    $obj->Mensaje = "";            	            		$obj->Mensaje = $client->call('Modificar',array($obj->Id,$obj->Nombre,$obj->Precio,$obj->Tipo));                    echo json_encode($obj);                    break;            case "ELIMINAR_MATERIAL":		                    $codigoBorrar=$_POST['codigoBorrar'];                                  		echo $client->call('Baja',array($codigoBorrar));		            		                    break;		    case "LOGOUT":			                    session_unset();            		session_destroy();                    if(isset($_COOKIE["user_mail"])) {                        $obj->Email = $_COOKIE["user_mail"];                        $obj->Exito = TRUE;                    }            			             	    echo json_encode($obj);                    break;    default:                    echo ":(";}